mapper.update:
update equip_dianjian_biaozhun
sbid = #{sbid}, sbname = #{sbname}, name = #{name}, biaozhun = #{biaozhun}, dept = #{dept}, team = #{team}, leibie = #{leibie}, zhouqi = #{zhouqi}, note = #{note}, zhoucishu = #{zhoucishu}, lasttime = #{lasttime}, flag = #{flag}, ordernum = #{ordernum},
where id = #{id}

mapper.select:
select id,sbid,sbname,name,dept,team,leibie,zhouqi,zhoucishu,lasttime, (select count(distinct djrq) from equip_dianjian_list b where a.sbid=b.sbid and a.dept=b.dept and a.team=b.team and b.djrq like concat(#{note}, '%'))as djcs from equip_dianjian_biaozhun a
and dept = #{dept}
order by dept,team,ordernum

select * from ( select dept ,team ,sum(zhoucishu) as zhoucishu,sum(djcs) as djcs from ( select sbname,name,dept,team,zhouqi,zhoucishu,(case when b.djcs>b.zhoucishu then b.zhoucishu else b.djcs end ) as djcs from (select id,sbid,sbname,name,dept,team,leibie,zhouqi,floor(zhoucishu*31/7) as zhoucishu,lasttime,(select count(distinct djrq) from equip_dianjian_list b where a.sbid=b.sbid and a.dept=b.dept and a.team=b.team and b.djrq like concat(#{note}, '%')) as djcs from equip_dianjian_biaozhun a) b ) c group by dept ,team )d
and dept = #{dept}
order by dept

select * from ( select dept ,sum(zhoucishu) as zhoucishu,sum(djcs) as djcs from ( select sbname,name,dept,team,zhouqi,zhoucishu,(case when b.djcs>b.zhoucishu then b.zhoucishu else b.djcs end ) as djcs from (select id,sbid,sbname,name,dept,team,leibie,zhouqi,floor(zhoucishu*31/7) as zhoucishu,lasttime,(select count(distinct djrq) from equip_dianjian_list b where a.sbid=b.sbid and a.dept=b.dept and a.team=b.team and b.djrq like concat(#{note}, '%')) as djcs from equip_dianjian_biaozhun a) b ) c group by dept )d
and dept = #{dept}
order by dept
